//New Database Seesion Mgt
// convoUser - Unique user id across channels
// ConvoTS - Timestamp of actual WCS Call
// convoSource - Source Channel Name, will be used in Convo
//             - Examples (Alexa,Google Home, Slack, Phone, WebChat, Mobile)
//convoPayload - actual return from WCS
// Database document will be calles session
// -- Index on convoUser and convoTS
// -- We will write all WCS turns into the table
// -- On retrieval  of the user session
//    -- We will search by index put back last 5 documents sorted by convoTS descending
//    -- assures first document will be the latest WCS user convo
//
// New fields for Reporting
// convoDialog - <context.system.dialog_stack[0].dialog_node> from WSC
// convoIntent - Root Vertical node. (Set ROOT-<name> in root vertical dialog node title in WCS that are applicable)
// convoDialogType - Type of node - for business scoring SUCCESS, FAIL, etc. (Set SUCCESS-<name> or FAIL-<name> in dialog node title in WCS) 
// convoCounter - unique counter after change of Root Vertical
// convoDialogElement - Intent or Entity name
// conovDialogConfidence - confidence score
// convoTransID = <username><convoIntent><date><convoCounter>     example:  dennisnoto-ROOT-ClaimStatus201701010823
//   Unique ID for convo to summarize statistics
// convoUtterance - user utterance
// convoAnswer - Watson answer

// Can chain  ROOT and FAIL  example ROOT-FAIL-<name>  This would signify a ROOT Vertical node with FAIL as the Type
// Can chain  ROOT and SUCCESS  example ROOT-SUCCESS-<name>  This would signify a ROOT Vertical node with SUCCESS as the Type

var d = new Date();
// MST Time Zone - 6 hours off UTC/GMT
var milliMST = 6*60*60*1000;
var localeDate = new Date(d.getTime()-milliMST);
var foundIntent = false;
var foundEntity = false;
msg.convoDialogIntents = "";
msg.convoDialogEntities = "";

// Set Utterance
if (typeof msg.payload.input !== "undefined") 
 {msg.convoUtterance = msg.payload.input.text;}

//Set Answer
if (typeof msg.payload.output !== "undefined") 
 { //msg.convoAnswer = msg.payload.output.text[0]
  msg.convoAnswer = "";
  var texts = msg.payload.output.text;
  for (i = 0; i < texts.length; i++) 
    {msg.convoAnswer = msg.convoAnswer + " " + texts[i]; }
     msg.convoAnswer = msg.convoAnswer.replace(/\[(.*?)\]/g, "");
  }
// Set DialogIntents and DialogEntities
if (typeof msg.payload.intents[0] !== "undefined")
 { foundIntent = true;
   for (i = 0; i < msg.payload.intents.length; i++) 
   { msg.convoDialogIntents = msg.convoDialogIntents + msg.payload.intents[i].intent + ","; }  
 }

if (typeof msg.payload.entities[0] !== "undefined")
 { foundEntity = true;
   for (i = 0; i < msg.payload.entities.length; i++) 
   { msg.convoDialogEntities = msg.convoDialogEntities + msg.payload.entities[i].entity + ","; }
 }
// Set confidence
if (foundIntent === true && foundEntity === true) 
    {msg.convoDialogConfidence = (msg.payload.intents[0].confidence + msg.payload.entities[0].confidence)/2; }
else if (foundIntent === true)  
    {msg.convoDialogConfidence = msg.payload.intents[0].confidence;}
else if (foundEntity === true)     
    {msg.convoDialogConfidence = msg.payload.entities[0].confidence;}
else 
    {msg.convoDialogConfidence = 0;}

// Set and change convoIntent by identifying Root Vertical Nodes. (Set ROOT-<name> in root vertical dialog node title in WCS that are applicable)
// Could find if node is ROOT by calling WCS api for dialog-node, but would be an extra api
if (typeof msg.payload.context.system.dialog_stack[0] !== "undefined") 
 {msg.convoDialogNode = msg.payload.context.system.dialog_stack[msg.payload.context.system.dialog_stack.length - 1].dialog_node;

  if (msg.convoDialogNode === "root")
    {msg.convoDialogNode = msg.payload.output.nodes_visited[msg.payload.output.nodes_visited.length - 1];}   
 }
else
  msg.convoDialogNode = "";

// Find convoIntent
if (typeof msg.session == "undefined") 
     if (msg.convoDialogNode.match(/ROOT-/g) )
         {msg.convoIntent = msg.convoDialogNode;
          msg.convoCounter = 1;}   
    else 
         {msg.convoIntent = "";
          msg.convoCounter = 0;} 

else if (msg.convoDialogNode.match(/ROOT-/g) )

   if (msg.convoDialogNode !== msg.session.convoIntent || msg.session.convoIntent === "") 
       {msg.convoIntent = msg.convoDialogNode;
        msg.convoCounter = msg.session.convoCounter + 1;}
    else  
       {msg.convoIntent = msg.session.convoIntent;
        msg.convoCounter = msg.session.convoCounter;}
else
   {msg.convoIntent = msg.session.convoIntent;
    msg.convoCounter = msg.session.convoCounter;}

// Set node type for scoring. (Set SUCCESS-<name> or FAIL-<name> in dialog node title in WCS) 
if (msg.convoDialogNode.match(/SUCCESS-/g) )
    {msg.convoDialogType = "SUCCESS";
     msg.convoContainmentScore = 100;}
else if (msg.convoDialogNode.match(/FAIL-/g) )
         {msg.convoDialogType = "FAIL";
          msg.convoContainmentScore = -100;}
else if (msg.convoDialogConfidence >= 0.7 )
         {msg.convoDialogType = "High_Confidence";
          msg.convoContainmentScore = 10;}
else if (msg.convoDialogConfidence >= 0.4 && msg.convoDialogConfidence < 0.7)
         {msg.convoDialogType = "Medium_Confidence";
          msg.convoContainmentScore = 5;}
else if (msg.convoDialogConfidence >= 0.2 && msg.convoDialogConfidence < 0.4)
         {msg.convoDialogType = "Low_Confidence";
          msg.convoContainmentScore = -5;}
else if (msg.convoDialogConfidence > 0 && msg.convoDialogConfidence < 0.2)
         {msg.convoDialogType = "No_Confidence";
          msg.convoContainmentScore = -10;}
else {msg.convoDialogType = "UNKNOWN";
      msg.convoContainmentScore = 0;}


var sessionMsg = {
    payload : {
    convoUser : msg.user,
    wcs_user_name : msg.params.username,
    wcs_password : msg.params.password,
    workspace_id : msg.params.workspace_id,
    convoTS : localeDate,
    convoSource : msg.source,
    convoIntent : msg.convoIntent,
    convoDialogNode : msg.convoDialogNode,
    convoDialogType : msg.convoDialogType,
    convoDialogIntents : msg.convoDialogIntents,
    convoDialogEntities : msg.convoDialogEntities,
    convoDialogConfidence : msg.convoDialogConfidence,
    convoContainmentScore : msg.convoContainmentScore,
    convoCounter : msg.convoCounter,
    convoTransID : msg.user + msg.convoIntent + localeDate.getFullYear()+ localeDate.getMonth()+1 + localeDate.getDate() + localeDate.getHours() + msg.convoCounter.toString(), 
    convoUtterance : msg.convoUtterance,
    convoAnswer : msg.convoAnswer,
    convoPayload : msg.payload
    }
};

return [msg,sessionMsg];

msg.savePayload = msg.payload

