for (i = 0; i < texts.length; i++) {

    if (texts[i].lastIndexOf("[") >= 0 ) {
       findindex = texts[i].lastIndexOf("[");
       texts[i] = texts[i].substring(0,findindex);
    } 
  totalText = totalText + " " + texts[i]
}

