function hex2a(hexx) {
    //node.warn("hexx: " + hexx);
    var hex = hexx.toString();//force conversion
    var str = '';
    for (var i = 0; i < hex.length; i += 2)
    {
        //node.log("hexx val: " + hex.substr(i, 2));
        str += String.fromCharCode(parseInt(hex.substr(i, 2), 16));

    }
    return str;
}

